INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U27_B2_I2', 'ingles-b2-U27', 7, 'multiple_choice', 'Choose the correct option to complete the sentences using participle clauses.', '{"prompt_es":"Choose the correct option to complete the sentences using participle clauses.","mastery_tag":"grammar:b2-participle-clauses","explanation":"Aquí, ''encouraged'' es un participio pasado que describe a los estudiantes en relación con la acción de ser motivados por el profesor.","stimulus_en":"The students, ___ by the teacher, completed their assignments.","options":[{"id":"o1","text":"encouraged"},{"id":"o2","text":"encouraging"},{"id":"o3","text":"to encourage"},{"id":"o4","text":"encourage"}],"correct_answer":"o1","complexity":3,"concept_tags":["grammar","participle","clauses","unit"]}')
