INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U27_B2_I3', 'ingles-b2-U27', 8, 'multiple_choice', 'Choose the correct option to complete the sentences using participle clauses.', '{"prompt_es":"Choose the correct option to complete the sentences using participle clauses.","mastery_tag":"grammar:b2-participle-clauses","explanation":"''Being'' se usa en esta situación para señalar que la naturaleza del libro es fascinante.","stimulus_en":"The book, ___ fascinating, kept me awake all night.","options":[{"id":"o1","text":"to be"},{"id":"o2","text":"being"},{"id":"o3","text":"been"},{"id":"o4","text":"be"}],"correct_answer":"o2","complexity":3,"concept_tags":["grammar","participle","clauses","unit"]}')
