VALUES ('B2_U27_B2_I4', 'ingles-b2-U27', 9, 'multiple_choice', 'Choose the correct option to complete the sentences using participle clauses.', '{"prompt_es":"Choose the correct option to complete the sentences using participle clauses.","mastery_tag":"grammar:b2-participle-clauses","explanation":"''Finished'' aquí indica una acción completada antes de que él saliera a jugar.","stimulus_en":"Having finished his homework, he went out to play.","options":[{"id":"o1","text":"Finishing"},{"id":"o2","text":"Finished"},{"id":"o3","text":"To finish"},{"id":"o4","text":"Having finish"}],"correct_answer":"o2","complexity":3,"concept_tags":["grammar","participle","clauses","unit"]}')
