VALUES ('B2_U27_B2_I5', 'ingles-b2-U27', 10, 'multiple_choice', 'Choose the correct option to complete the sentences using participle clauses.', '{"prompt_es":"Choose the correct option to complete the sentences using participle clauses.","mastery_tag":"grammar:b2-participle-clauses","explanation":"''Having been'' indica que ella había experimentado la acción de estar molestada antes de dejar la reunión.","stimulus_en":"___ upset by the news, she left the meeting early.","options":[{"id":"o1","text":"Being"},{"id":"o2","text":"Having been"},{"id":"o3","text":"To be"},{"id":"o4","text":"Is being"}],"correct_answer":"o2","complexity":3,"concept_tags":["grammar","participle","clauses","unit"]}')
