VALUES ('B2_U27_B3_I1', 'ingles-b2-U27', 11, 'multiple_choice', 'What is a key feature of an article review?', '{"prompt_es":"What is a key feature of an article review?","mastery_tag":"writing:b2-reviews-articles","explanation":"Una revisión de artículo a menudo incluye la opinión personal del autor sobre el contenido revisado.","stimulus_en":"","options":[{"id":"o1","text":"Personal opinion"},{"id":"o2","text":"Storytelling"},{"id":"o3","text":"Data analysis"},{"id":"o4","text":"Fiction"}],"correct_answer":"o1","complexity":3,"concept_tags":["writing","reviews","articles","unit","grammar"]}')
