VALUES ('B2_U27_B3_I4', 'ingles-b2-U27', 14, 'multiple_choice', 'In a product review, what should the author primarily evaluate?', '{"prompt_es":"In a product review, what should the author primarily evaluate?","mastery_tag":"writing:b2-reviews-articles","explanation":"En una reseña de producto, el enfoque principal debe estar en las características y el rendimiento del producto.","stimulus_en":"","options":[{"id":"o1","text":"Author’s background"},{"id":"o2","text":"Market trends"},{"id":"o3","text":"Product features"},{"id":"o4","text":"Personal thoughts"}],"correct_answer":"o3","complexity":3,"concept_tags":["writing","reviews","articles","unit","grammar"]}')
