VALUES ('B2_U27_B3_I5', 'ingles-b2-U27', 15, 'multiple_choice', 'What writing style is typically used for formal articles?', '{"prompt_es":"What writing style is typically used for formal articles?","mastery_tag":"writing:b2-reviews-articles","explanation":"Los artículos formales suelen seguir un estilo académico que es más riguroso y objetivo.","stimulus_en":"","options":[{"id":"o1","text":"Casual"},{"id":"o2","text":"Conversational"},{"id":"o3","text":"Academic"},{"id":"o4","text":"Humorous"}],"correct_answer":"o3","complexity":3,"concept_tags":["writing","reviews","articles","unit","grammar"]}')
