VALUES ('B2_U29_B2_I3', 'ingles-b2-U29', 8, 'multiple_choice', 'What is a good way to engage an audience during a lecture?', '{"prompt_es":"What is a good way to engage an audience during a lecture?","mastery_tag":"listening:b2-lectures","explanation":"Hacer preguntas durante una conferencia involucra al público y estimula su interés y participación en el tema.","stimulus_en":"","options":[{"id":"o1","text":"Reading slides verbatim"},{"id":"o2","text":"Asking questions"},{"id":"o3","text":"Ignoring the audience"},{"id":"o4","text":"Speaking quickly"}],"correct_answer":"o2","complexity":3,"concept_tags":["listening","lectures","unit","grammar"]}')
