VALUES ('B2_U29_B2_I4', 'ingles-b2-U29', 9, 'multiple_choice', 'Which of the following is NOT typically included in a lecture?', '{"prompt_es":"Which of the following is NOT typically included in a lecture?","mastery_tag":"listening:b2-lectures","explanation":"Una conferencia generalmente incluye una introducción, un cuerpo principal y una conclusión, pero no actividades sociales.","stimulus_en":"","options":[{"id":"o1","text":"Introduction"},{"id":"o2","text":"Main body"},{"id":"o3","text":"Conclusion"},{"id":"o4","text":"Social activity"}],"correct_answer":"o4","complexity":3,"concept_tags":["listening","lectures","unit","grammar"]}')
