VALUES ('B2_U29_B3_I1', 'ingles-b2-U29', 11, 'multiple_choice', 'Choose the best option that completes the idiomatic expression or phrase.', '{"prompt_es":"Choose the best option that completes the idiomatic expression or phrase.","mastery_tag":"vocabulary:b2-idiomatic","explanation":"La expresión ''not to take it personally'' significa no sentirse ofendido por las acciones o palabras de otros.","stimulus_en":"He was really upset after the meeting, but I told him not to take it _____.","options":[{"id":"o1","text":"personally"},{"id":"o2","text":"seriously"},{"id":"o3","text":"lightly"},{"id":"o4","text":"casually"}],"correct_answer":"o1","complexity":3,"concept_tags":["vocabulary","idiomatic","unit","grammar"]}')
