INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U29_B3_I2', 'ingles-b2-U29', 12, 'multiple_choice', 'Choose the best option that completes the idiomatic expression or phrase.', '{"prompt_es":"Choose the best option that completes the idiomatic expression or phrase.","mastery_tag":"vocabulary:b2-idiomatic","explanation":"La expresión ''way off'' se usa para indicar que algo está muy lejos de lo correcto o esperado.","stimulus_en":"I thought I knew the answer, but it turns out I was way _____.","options":[{"id":"o1","text":"off"},{"id":"o2","text":"away"},{"id":"o3","text":"out"},{"id":"o4","text":"down"}],"correct_answer":"o1","complexity":3,"concept_tags":["vocabulary","idiomatic","unit","grammar"]}')
