INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U29_B3_I3', 'ingles-b2-U29', 13, 'multiple_choice', 'Choose the best option that completes the idiomatic expression or phrase.', '{"prompt_es":"Choose the best option that completes the idiomatic expression or phrase.","mastery_tag":"vocabulary:b2-idiomatic","explanation":"La expresión ''think outside the box'' significa ser creativo o innovador en la resolución de problemas.","stimulus_en":"When it comes to traveling, she really knows how to think outside the _____.","options":[{"id":"o1","text":"box"},{"id":"o2","text":"line"},{"id":"o3","text":"circle"},{"id":"o4","text":"frame"}],"correct_answer":"o1","complexity":3,"concept_tags":["vocabulary","idiomatic","unit","grammar"]}')
