INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U29_B3_I4', 'ingles-b2-U29', 14, 'multiple_choice', 'Choose the best option that completes the idiomatic expression or phrase.', '{"prompt_es":"Choose the best option that completes the idiomatic expression or phrase.","mastery_tag":"vocabulary:b2-idiomatic","explanation":"La expresión ''on cloud nine'' significa estar muy feliz o eufórico.","stimulus_en":"He is on cloud nine since he got the promotion; he is so _____.","options":[{"id":"o1","text":"happy"},{"id":"o2","text":"angry"},{"id":"o3","text":"busy"},{"id":"o4","text":"tired"}],"correct_answer":"o1","complexity":3,"concept_tags":["vocabulary","idiomatic","unit","grammar"]}')
