INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U29_B3_I5', 'ingles-b2-U29', 15, 'multiple_choice', 'Choose the best option that completes the idiomatic expression or phrase.', '{"prompt_es":"Choose the best option that completes the idiomatic expression or phrase.","mastery_tag":"vocabulary:b2-idiomatic","explanation":"La expresión ''sit on the fence'' significa no tomar una decisión o no comprometerse con una opinión.","stimulus_en":"You can''t just sit on the _____. You need to take action.","options":[{"id":"o1","text":"fence"},{"id":"o2","text":"couch"},{"id":"o3","text":"bed"},{"id":"o4","text":"floor"}],"correct_answer":"o1","complexity":3,"concept_tags":["vocabulary","idiomatic","unit","grammar"]}')
