INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U30_B1_I1', 'ingles-b2-U30', 1, 'fill_blanks', 'Fill in the blanks with the correct perfect modal verb.', '{"prompt_es":"Fill in the blanks with the correct perfect modal verb.","mastery_tag":"grammar:b2-perfect-modals","main_text":"She ___ (must) have taken the wrong train; she is late again.","correct_answer":["must","should","could","might","must"],"explanation":"(1) Aquí se usa ''must have'' para indicar una fuerte suposición sobre algo que sucedió en el pasado.\n(2) El uso de ''should have'' indica que se esperaba que completaran el proyecto en el pasado.\n(3) ''Could have'' sugiere una posibilidad que no se realizó, indicando que tenía la opción de llamar.\n(4) ''Might have'' expresa una posibilidad en el pasado de que la persona la haya visto en la fiesta.\n(5) ''Must have'' se utiliza aquí para suponer que es muy probable que hayan escuchado las noticias.","complexity":3,"concept_tags":["grammar","perfect","modals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U30_B2_I1', 'ingles-b2-U30', 2, 'multiple_choice', 'What is the primary purpose of a review?', '{"prompt_es":"What is the primary purpose of a review?","mastery_tag":"writing:b2-reviews-articles","explanation":"El propósito principal de una reseña es evaluar un producto, servicio o experiencia. Las reseñas ayudan a los lectores a tomar decisiones informadas.","stimulus_en":"","options":[{"id":"o1","text":"To inform"},{"id":"o2","text":"To entertain"},{"id":"o3","text":"To persuade"},{"id":"o4","text":"To evaluate"}],"correct_answer":"o4","complexity":3,"concept_tags":["writing","reviews","articles","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U30_B2_I2', 'ingles-b2-U30', 3, 'multiple_choice', 'Which of the following should NOT be included in a professional article?', '{"prompt_es":"Which of the following should NOT be included in a professional article?","mastery_tag":"writing:b2-reviews-articles","explanation":"Un artículo profesional debe basarse en la evidencia y se espera que se mantenga objetivo. Las opiniones personales pueden no ser apropiadas.","stimulus_en":"","options":[{"id":"o1","text":"Personal opinions"},{"id":"o2","text":"Factual evidence"},{"id":"o3","text":"Citations"},{"id":"o4","text":"Research data"}],"correct_answer":"o1","complexity":3,"concept_tags":["writing","reviews","articles","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U30_B2_I3', 'ingles-b2-U30', 4, 'multiple_choice', 'What is a key feature of a well-written review?', '{"prompt_es":"What is a key feature of a well-written review?","mastery_tag":"writing:b2-reviews-articles","explanation":"Una buena reseña debe ofrecer una perspectiva equilibrada, discutiendo tanto los aspectos positivos como negativos del sujeto.","stimulus_en":"","options":[{"id":"o1","text":"Lengthy descriptions"},{"id":"o2","text":"Balanced viewpoints"},{"id":"o3","text":"Sole focus on negatives"},{"id":"o4","text":"Use of jargon"}],"correct_answer":"o2","complexity":3,"concept_tags":["writing","reviews","articles","unit","grammar"]}')
