INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U30_B2_I4', 'ingles-b2-U30', 5, 'multiple_choice', 'Choose the best option for each question based on your understanding of reviews and articles.', '{"prompt_es":"Choose the best option for each question based on your understanding of reviews and articles.","mastery_tag":"writing:b2-reviews-articles","explanation":"Un artículo de opinión se centra en las creencias y perspectivas del autor sobre un tema, presentando argumentos que lo respaldan.","stimulus_en":"In an opinion piece, the writer should primarily focus on:","options":[{"id":"o1","text":"Analysis of data"},{"id":"o2","text":"Personal beliefs"},{"id":"o3","text":"Historical context"},{"id":"o4","text":"Literary style"}],"correct_answer":"o2","complexity":3,"concept_tags":["writing","reviews","articles","unit","grammar"]}')
