VALUES ('B2_U30_B2_I5', 'ingles-b2-U30', 6, 'multiple_choice', 'Which element is crucial for attracting readers to an article?', '{"prompt_es":"Which element is crucial for attracting readers to an article?","mastery_tag":"writing:b2-reviews-articles","explanation":"Los titulares atractivos capturan la atención del lector y son un componente esencial de un artículo efectivo.","stimulus_en":"","options":[{"id":"o1","text":"Abstract titles"},{"id":"o2","text":"Boring introductions"},{"id":"o3","text":"Engaging headlines"},{"id":"o4","text":"Complex vocabulary"}],"correct_answer":"o3","complexity":3,"concept_tags":["writing","reviews","articles","unit","grammar"]}')
