VALUES ('B2_U30_B3_I1', 'ingles-b2-U30', 7, 'multiple_choice', 'What was the main focus of the lecture in 1967?', '{"prompt_es":"What was the main focus of the lecture in 1967?","mastery_tag":"listening:b2-lectures","explanation":"La respuesta correcta es ''Technological advancements'' porque el enfoque era sobre cómo la tecnología estaba cambiando la sociedad en ese año.","stimulus_en":"","options":[{"id":"o1","text":"Historical events"},{"id":"o2","text":"Technological advancements"},{"id":"o3","text":"Cultural changes"},{"id":"o4","text":"Political movements"}],"correct_answer":"o2","complexity":3,"concept_tags":["listening","lectures","unit","grammar"]}')
