INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U1_B1_I1', 'ingles-c1-unit1', 1, 'transformation', 'Reescribe la frase comenzando con la expresión negativa para dar énfasis (Inversion):', '{"prompt_es":"Reescribe la frase comenzando con la expresión negativa para dar énfasis (Inversion):","stimulus_en":"I have never seen such a beautiful sunset. (NEVER)","correct_answer":"Never have I seen such a beautiful sunset.","explanation":"When we start a sentence with a negative adverb like ''never'', we use question word order (auxiliary before subject).","concept_tags":["grammar","inversion","emphasis"],"complexity":3}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U1_B1_I2', 'ingles-c1-unit1', 2, 'multiple_choice', 'Selecciona la opción gramaticalmente correcta:', '{"prompt_es":"Selecciona la opción gramaticalmente correcta:","options":[{"id":"o1","text":"Little he knew about the surprise waiting for him."},{"id":"o2","text":"Little did he know about the surprise waiting for him."},{"id":"o3","text":"Little he did know about the surprise waiting for him."}],"correct_answer":"o2","explanation":"''Little'' at the beginning of a sentence requires inversion of the auxiliary ''did''.","concept_tags":["grammar","inversion"],"complexity":3}')
