INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U3_B1_I1', 'ingles-c1-U3', 1, 'multiple_choice', 'What is the primary purpose of a thesis statement in an academic essay?', '{"prompt_es":"What is the primary purpose of a thesis statement in an academic essay?","mastery_tag":"writing:c1-academic-writing","explanation":"El propósito principal de una declaración de tesis es presentar la idea central que se argumentará en el ensayo.","stimulus_en":"","options":[{"id":"o1","text":"To summarize the essay"},{"id":"o2","text":"To introduce the topic"},{"id":"o3","text":"To present the main argument"},{"id":"o4","text":"To conclude the essay"}],"correct_answer":"o3"}')
