INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U3_B1_I3', 'ingles-c1-U3', 3, 'multiple_choice', 'Which of the following is NOT a typical component of an academic report?', '{"prompt_es":"Which of the following is NOT a typical component of an academic report?","mastery_tag":"writing:c1-academic-writing","explanation":"Las opiniones personales no son componentes típicos de un informe académico ya que se basa en la evidencia y la investigación objetiva.","stimulus_en":"","options":[{"id":"o1","text":"Introduction"},{"id":"o2","text":"Methodology"},{"id":"o3","text":"Personal opinion"},{"id":"o4","text":"Conclusion"}],"correct_answer":"o3"}')
