INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U3_B1_I4', 'ingles-c1-U3', 4, 'multiple_choice', 'What is the use of citations in academic writing?', '{"prompt_es":"What is the use of citations in academic writing?","mastery_tag":"writing:c1-academic-writing","explanation":"Las citas se utilizan para respaldar afirmaciones con evidencia y para dar crédito a las fuentes originales.","stimulus_en":"","options":[{"id":"o1","text":"To provide personal experiences"},{"id":"o2","text":"To support claims with evidence"},{"id":"o3","text":"To make the text longer"},{"id":"o4","text":"To express personal views"}],"correct_answer":"o2"}')
