INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U3_B1_I5', 'ingles-c1-U3', 5, 'multiple_choice', 'Why is it important to have a clear structure in an academic essay?', '{"prompt_es":"Why is it important to have a clear structure in an academic essay?","mastery_tag":"writing:c1-academic-writing","explanation":"Una estructura clara facilita la comprensión del argumento y la lógica del ensayo, lo cual es crucial en la escritura académica.","stimulus_en":"","options":[{"id":"o1","text":"It makes it more interesting"},{"id":"o2","text":"It helps the reader understand the argument"},{"id":"o3","text":"It allows for more personal expression"},{"id":"o4","text":"It can increase word count"}],"correct_answer":"o2"}')
