VALUES ('C1_U6_B1_I5', 'ingles-c1-U6', 5, 'multiple_choice', 'Choose the correct option to complete each sentence related to academic writing.', '{"prompt_es":"Choose the correct option to complete each sentence related to academic writing.","mastery_tag":"writing:c1-academic-writing","explanation":"Las citas son necesarias para dar crédito a las fuentes y evitar el plagio.","stimulus_en":"Citations are crucial in academic writing to avoid _______.","options":[{"id":"o1","text":"plagiarism"},{"id":"o2","text":"innovation"},{"id":"o3","text":"clarity"},{"id":"o4","text":"credibility"}],"correct_answer":"o1"}')
