INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U7_B3_I3', 'ingles-c1-U7', 13, 'multiple_choice', 'How should recommendations be presented in a proposal?', '{"prompt_es":"How should recommendations be presented in a proposal?","mastery_tag":"writing:c1-proposals","explanation":"Las recomendaciones en una propuesta deben presentarse de manera clara y segura para que el lector comprenda su importancia.","stimulus_en":"","options":[{"id":"o1","text":"As optional suggestions"},{"id":"o2","text":"In a vague manner"},{"id":"o3","text":"Clearly and confidently"},{"id":"o4","text":"With extensive technical jargon"}],"correct_answer":"o3"}')
