INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U7_B3_I4', 'ingles-c1-U7', 14, 'multiple_choice', 'What is a common mistake when writing proposals?', '{"prompt_es":"What is a common mistake when writing proposals?","mastery_tag":"writing:c1-proposals","explanation":"Un error común al redactar propuestas es no incluir un resumen ejecutivo, que proporciona una visión general clara del documento.","stimulus_en":"","options":[{"id":"o1","text":"Not including an executive summary"},{"id":"o2","text":"Using bullet points"},{"id":"o3","text":"Writing in the first person"},{"id":"o4","text":"Including too many visuals"}],"correct_answer":"o1"}')
