VALUES ('C1_U7_B3_I5', 'ingles-c1-U7', 15, 'multiple_choice', 'When evaluating a proposal, which factor is least important?', '{"prompt_es":"When evaluating a proposal, which factor is least important?","mastery_tag":"writing:c1-proposals","explanation":"Las relaciones personales pueden influir, pero no son un factor determinante en la evaluación objetiva de una propuesta.","stimulus_en":"","options":[{"id":"o1","text":"Feasibility"},{"id":"o2","text":"Budget"},{"id":"o3","text":"Personal relationships"},{"id":"o4","text":"Expected outcomes"}],"correct_answer":"o3"}')
