VALUES ('C1_U8_B1_I3', 'ingles-c1-U8', 3, 'multiple_choice', 'What is a key characteristic of effective body language during a presentation?', '{"prompt_es":"What is a key characteristic of effective body language during a presentation?","mastery_tag":"speaking:c1-public-speaking","explanation":"Los gestos abiertos transmiten confianza y ayudan a conectar con la audiencia.","stimulus_en":"","options":[{"id":"o1","text":"Avoiding eye contact"},{"id":"o2","text":"Open gestures"},{"id":"o3","text":"Standing still"},{"id":"o4","text":"Crossed arms"}],"correct_answer":"o2"}')
