VALUES ('C1_U8_B1_I5', 'ingles-c1-U8', 5, 'multiple_choice', 'What is the purpose of using visual aids in a presentation?', '{"prompt_es":"What is the purpose of using visual aids in a presentation?","mastery_tag":"speaking:c1-public-speaking","explanation":"Las ayudas visuales deben complementar el contenido y hacer que los puntos clave sean más claros.","stimulus_en":"","options":[{"id":"o1","text":"To distract the audience"},{"id":"o2","text":"To support key points"},{"id":"o3","text":"To fill time"},{"id":"o4","text":"To look professional"}],"correct_answer":"o2"}')
