INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U8_B2_I1', 'ingles-c1-U8', 6, 'multiple_choice', 'Choose the best option that completes each sentence with the most appropriate nuanced expression.', '{"prompt_es":"Choose the best option that completes each sentence with the most appropriate nuanced expression.","mastery_tag":"vocabulary:c1-nuanced","explanation":"En la comunicación profesional, una ''concesión'' implica un reconocimiento que puede ser sutil en su impacto.","stimulus_en":"In professional communication, a ''concession'' often implies a ___ that can be subtle in its impact.","options":[{"id":"o1","text":"favor"},{"id":"o2","text":"warning"},{"id":"o3","text":"acknowledgment"},{"id":"o4","text":"complaint"}],"correct_answer":"o3"}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U8_B2_I2', 'ingles-c1-U8', 7, 'multiple_choice', 'Choose the best option that completes each sentence with the most appropriate nuanced expression.', '{"prompt_es":"Choose the best option that completes each sentence with the most appropriate nuanced expression.","mastery_tag":"vocabulary:c1-nuanced","explanation":"Usar el término ''desafiante'' en lugar de ''difícil'' transmite una actitud más positiva.","stimulus_en":"Using the term ''challenging'' instead of ''difficult'' conveys a more ___ attitude.","options":[{"id":"o1","text":"positive"},{"id":"o2","text":"negative"},{"id":"o3","text":"neutral"},{"id":"o4","text":"ambiguous"}],"correct_answer":"o1"}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U8_B2_I3', 'ingles-c1-U8', 8, 'multiple_choice', 'Choose the best option that completes each sentence with the most appropriate nuanced expression.', '{"prompt_es":"Choose the best option that completes each sentence with the most appropriate nuanced expression.","mastery_tag":"vocabulary:c1-nuanced","explanation":"Describir la idea de un colega como ''intrigante'' sugiere una perspectiva curiosa en lugar de una actitud despectiva.","stimulus_en":"To describe a colleague’s idea as ''intriguing'' suggests a ___ perspective rather than a dismissive one.","options":[{"id":"o1","text":"hostile"},{"id":"o2","text":"curious"},{"id":"o3","text":"indifferent"},{"id":"o4","text":"critical"}],"correct_answer":"o2"}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U8_B2_I4', 'ingles-c1-U8', 9, 'multiple_choice', 'Choose the best option that completes each sentence with the most appropriate nuanced expression.', '{"prompt_es":"Choose the best option that completes each sentence with the most appropriate nuanced expression.","mastery_tag":"vocabulary:c1-nuanced","explanation":"El uso de ''limitado'' en lugar de ''pobre'' puede reflejar una evaluación más cuidadosa de los recursos.","stimulus_en":"The use of ''limited'' instead of ''poor'' can reflect a more ___ assessment of resources.","options":[{"id":"o1","text":"harsh"},{"id":"o2","text":"tactful"},{"id":"o3","text":"blunt"},{"id":"o4","text":"ambiguous"}],"correct_answer":"o2"}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U8_B2_I5', 'ingles-c1-U8', 10, 'multiple_choice', 'Choose the best option that completes each sentence with the most appropriate nuanced expression.', '{"prompt_es":"Choose the best option that completes each sentence with the most appropriate nuanced expression.","mastery_tag":"vocabulary:c1-nuanced","explanation":"Al proporcionar comentarios, una ''sugerencia'' suele ser más constructiva que una crítica directa.","stimulus_en":"When providing feedback, a ''suggestion'' is often more ___ than a direct critique.","options":[{"id":"o1","text":"harsh"},{"id":"o2","text":"unnecessary"},{"id":"o3","text":"constructive"},{"id":"o4","text":"irrelevant"}],"correct_answer":"o3"}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U8_B3_I1', 'ingles-c1-U8', 11, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''neither'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''neither'':","mastery_tag":"grammar:c1-ellipsis","explanation":"En esta oración, usamos ''neither'' para sustituir la repetición de ''she didn''t see the problem''.","stimulus_en":"He didn''t see the problem, and she didn''t see it either.\n\nNeither did she see ...","correct_answer":"the problem.","example":"neither"}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U8_B3_I2', 'ingles-c1-U8', 12, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''as well'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''as well'':","mastery_tag":"grammar:c1-ellipsis","explanation":"Aquí, ''as well as'' permite omitir la repetición de ''the project was delayed''.","stimulus_en":"The project was delayed, and the budget was affected too.\n\nThe budget was affected too, ...","correct_answer":"as well as the project.","example":"as well"}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U8_B3_I3', 'ingles-c1-U8', 13, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''also'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''also'':","mastery_tag":"grammar:c1-ellipsis","explanation":"Se utiliza ''also'' para evitar la repetición de ''the presentation'' en la segunda parte de la oración.","stimulus_en":"Tom enjoyed the presentation, and Mary did too.\n\nMary also enjoyed ...","correct_answer":"the presentation.","example":"also"}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U8_B3_I4', 'ingles-c1-U8', 14, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''too'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''too'':","mastery_tag":"grammar:c1-ellipsis","explanation":"El uso de ''too'' aquí nos permite omitir la repetición de ''the report''.","stimulus_en":"I recommend the report, and John recommends it as well.\n\nJohn recommends it, too, ...","correct_answer":"the report.","example":"too"}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U8_B3_I5', 'ingles-c1-U8', 15, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''will'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''will'':","mastery_tag":"grammar:c1-ellipsis","explanation":"Aquí, usamos ''too'' para no repetir ''lead it'' en la segunda parte de la oración.","stimulus_en":"Our team will lead the project, and the marketing team will lead it as well.\n\nThe marketing team will lead the project, ...","correct_answer":"too.","example":"will"}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;

