INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U9_B1_I1', 'ingles-c1-U9', 1, 'multiple_choice', 'What reason does Mark give for wanting to stay indoors this weekend?', '{"prompt_es":"What reason does Mark give for wanting to stay indoors this weekend?","mastery_tag":"listening:c1-complex-audio","explanation":"Mark expresses disinterest in outdoor activities due to his dislike of the rain.","stimulus_en":"You will hear a conversation between two friends discussing their plans for the weekend. They talk about the weather, their favorite activities, and some recent movies they''ve seen.","options":[{"id":"o1","text":"He has a cold"},{"id":"o2","text":"He prefers movies"},{"id":"o3","text":"He has work"},{"id":"o4","text":"He dislikes the weather"}],"correct_answer":"o4"}')
