INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U9_B1_I4', 'ingles-c1-U9', 4, 'multiple_choice', 'What activity do the friends plan to do instead of going out?', '{"prompt_es":"What activity do the friends plan to do instead of going out?","mastery_tag":"listening:c1-complex-audio","explanation":"Their discussion centers on binge-watching movies as an alternative to outdoor plans.","stimulus_en":"You will hear a conversation between two friends discussing their plans for the weekend. They talk about the weather, their favorite activities, and some recent movies they''ve seen.","options":[{"id":"o1","text":"Visit a museum"},{"id":"o2","text":"Watch films"},{"id":"o3","text":"Go hiking"},{"id":"o4","text":"Cook together"}],"correct_answer":"o2"}')
