INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U9_B1_I5', 'ingles-c1-U9', 5, 'multiple_choice', 'What underlying message is conveyed about their friendship?', '{"prompt_es":"What underlying message is conveyed about their friendship?","mastery_tag":"listening:c1-complex-audio","explanation":"Their agreement on movies and plans shows a strong bond and shared interests.","stimulus_en":"You will hear a conversation between two friends discussing their plans for the weekend. They talk about the weather, their favorite activities, and some recent movies they''ve seen.","options":[{"id":"o1","text":"They often argue"},{"id":"o2","text":"They enjoy similar tastes"},{"id":"o3","text":"They dislike each other"},{"id":"o4","text":"They rarely meet"}],"correct_answer":"o2"}')
