INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U10_B3_I5', 'ingles-c1-U10', 11, 'multiple_choice', 'Which term is synonymous with ''delicate'' when referring to a subtle difference or distinction?', '{"prompt_es":"Which term is synonymous with ''delicate'' when referring to a subtle difference or distinction?","mastery_tag":"vocabulary:c1-nuanced","explanation":"''Faint'' se refiere a algo débil o sutil, que es adecuado cuando se discuten diferencias delicadas.","stimulus_en":"","options":[{"id":"o1","text":"Robust"},{"id":"o2","text":"Faint"},{"id":"o3","text":"Harsh"},{"id":"o4","text":"Obvious"}],"correct_answer":"o2"}')
