INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U4_B1_I1', 'ingles-c1-U4', 1, 'word_formation', 'Fill in the blanks with the correct form of the word provided.', '{"prompt_es":"Fill in the blanks with the correct form of the word provided.","mastery_tag":"vocabulary:c1-academic","main_text":"The (1)___ of the study showed significant results. The researchers conducted a thorough (2)___ to support their (3)___ about the effects of climate change. This (4)___ helps to understand the complex relationships in environmental science. Future (5)___ will focus on expanding this research.","correct_answer":["ANALYSIS","INVESTIGATION","THEORY","CONTRIBUTION","STUDIES"],"example":"ANALYZE, INVESTIGATE, THEORIZE, CONTRIBUTE, STUDY","explanation":"(1) undefined\n(2) undefined\n(3) undefined\n(4) undefined\n(5) undefined"}')
