INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U4_B3_I1', 'ingles-c1-U4', 3, 'fill_blanks', 'Complete the sentences with the appropriate words.', '{"prompt_es":"Complete the sentences with the appropriate words.","mastery_tag":"speaking:c1-fluent-interaction","explanation":"Es importante que las conversaciones tengan un flujo natural para involucrar al oyente.","stimulus_en":"It''s important to maintain a sense of ___ during conversations to engage your listener.","main_text":"Fill in the blanks with suitable words to enhance fluent interaction in conversations.","correct_answer":"flow"}')
