VALUES ('C1_U5_B1_I5', 'ingles-c1-U5', 5, 'multiple_choice', 'What does ''to beat around the bush'' mean?', '{"prompt_es":"What does ''to beat around the bush'' mean?","mastery_tag":"vocabulary:c1-nuanced","explanation":"Implica no abordar el tema principal de manera directa.","stimulus_en":"","options":[{"id":"o1","text":"To be direct"},{"id":"o2","text":"To avoid getting to the point"},{"id":"o3","text":"To speak quickly"},{"id":"o4","text":"To explain clearly"}],"correct_answer":"o2"}')
