VALUES ('C1_U6_B1_I1', 'ingles-c1-U6', 1, 'multiple_choice', 'Choose the correct option to complete each sentence related to academic writing.', '{"prompt_es":"Choose the correct option to complete each sentence related to academic writing.","mastery_tag":"writing:c1-academic-writing","explanation":"En los ensayos académicos, se espera que el tono sea objetivo y profesional.","stimulus_en":"In academic essays, it is essential to maintain a tone that is _______.","options":[{"id":"o1","text":"formal"},{"id":"o2","text":"casual"},{"id":"o3","text":"colloquial"},{"id":"o4","text":"emotional"}],"correct_answer":"o1"}')
