VALUES ('C1_U6_B1_I2', 'ingles-c1-U6', 2, 'multiple_choice', 'Choose the correct option to complete each sentence related to academic writing.', '{"prompt_es":"Choose the correct option to complete each sentence related to academic writing.","mastery_tag":"writing:c1-academic-writing","explanation":"Una declaración de tesis clara es fundamental para guiar el ensayo.","stimulus_en":"A well-structured essay should begin with a clear _______.","options":[{"id":"o1","text":"thesis statement"},{"id":"o2","text":"summary"},{"id":"o3","text":"conclusion"},{"id":"o4","text":"review"}],"correct_answer":"o1"}')
