VALUES ('C1_U6_B1_I4', 'ingles-c1-U6', 4, 'multiple_choice', 'Choose the correct option to complete each sentence related to academic writing.', '{"prompt_es":"Choose the correct option to complete each sentence related to academic writing.","mastery_tag":"writing:c1-academic-writing","explanation":"El resumen (abstract) debe ofrecer una visión general clara del trabajo académico.","stimulus_en":"The _______ of an academic paper provides a concise summary of its content.","options":[{"id":"o1","text":"abstract"},{"id":"o2","text":"conclusion"},{"id":"o3","text":"introduction"},{"id":"o4","text":"chapter"}],"correct_answer":"o1"}')
