VALUES ('C1_U6_B3_I1', 'ingles-c1-U6', 7, 'multiple_choice', 'What is the primary goal of a persuasive speech?', '{"prompt_es":"What is the primary goal of a persuasive speech?","mastery_tag":"speaking:c1-public-speaking","explanation":"El objetivo principal de un discurso persuasivo es convencer a la audiencia de un punto de vista o acción.","stimulus_en":"","options":[{"id":"o1","text":"To inform the audience"},{"id":"o2","text":"To entertain the audience"},{"id":"o3","text":"To convince the audience"},{"id":"o4","text":"To describe a process"}],"correct_answer":"o3"}')
