VALUES ('C1_U6_B3_I2', 'ingles-c1-U6', 8, 'multiple_choice', 'Which technique is NOT commonly used in effective public speaking?', '{"prompt_es":"Which technique is NOT commonly used in effective public speaking?","mastery_tag":"speaking:c1-public-speaking","explanation":"El uso de gestos exagerados puede distraer en lugar de ayudar a transmitir el mensaje.","stimulus_en":"","options":[{"id":"o1","text":"Use of anecdotes"},{"id":"o2","text":"Exaggerated gestures"},{"id":"o3","text":"Clear structure"},{"id":"o4","text":"Rhetorical questions"}],"correct_answer":"o2"}')
