VALUES ('C1_U6_B3_I4', 'ingles-c1-U6', 10, 'multiple_choice', 'What is an essential part of a well-structured presentation?', '{"prompt_es":"What is an essential part of a well-structured presentation?","mastery_tag":"speaking:c1-public-speaking","explanation":"Puntos principales claros son fundamentales para guiar a la audiencia a través de la presentación.","stimulus_en":"","options":[{"id":"o1","text":"A long introduction"},{"id":"o2","text":"Clear main points"},{"id":"o3","text":"Complex vocabulary"},{"id":"o4","text":"A summary only"}],"correct_answer":"o2"}')
