VALUES ('C1_U8_B1_I1', 'ingles-c1-U8', 1, 'multiple_choice', 'What is the main purpose of the introduction in a presentation?', '{"prompt_es":"What is the main purpose of the introduction in a presentation?","mastery_tag":"speaking:c1-public-speaking","explanation":"La introducción tiene como objetivo captar la atención del público y establecer el tono de la presentación.","stimulus_en":"","options":[{"id":"o1","text":"To summarize"},{"id":"o2","text":"To engage the audience"},{"id":"o3","text":"To conclude"},{"id":"o4","text":"To provide examples"}],"correct_answer":"o2"}')
