INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U8_B1_I4', 'ingles-c1-U8', 4, 'multiple_choice', 'How should a speaker handle questions from the audience?', '{"prompt_es":"How should a speaker handle questions from the audience?","mastery_tag":"speaking:c1-public-speaking","explanation":"Hacer una pausa y escuchar demuestra respeto por la opinión del público y mejora la interacción.","stimulus_en":"","options":[{"id":"o1","text":"Ignore them"},{"id":"o2","text":"Answer quickly"},{"id":"o3","text":"Pause and listen"},{"id":"o4","text":"Redirect to another topic"}],"correct_answer":"o3"}')
