INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U8_B2_I2', 'ingles-c1-U8', 7, 'multiple_choice', 'Choose the best option that completes each sentence with the most appropriate nuanced expression.', '{"prompt_es":"Choose the best option that completes each sentence with the most appropriate nuanced expression.","mastery_tag":"vocabulary:c1-nuanced","explanation":"Usar el término ''desafiante'' en lugar de ''difícil'' transmite una actitud más positiva.","stimulus_en":"Using the term ''challenging'' instead of ''difficult'' conveys a more ___ attitude.","options":[{"id":"o1","text":"positive"},{"id":"o2","text":"negative"},{"id":"o3","text":"neutral"},{"id":"o4","text":"ambiguous"}],"correct_answer":"o1"}')
