INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U8_B2_I4', 'ingles-c1-U8', 9, 'multiple_choice', 'Choose the best option that completes each sentence with the most appropriate nuanced expression.', '{"prompt_es":"Choose the best option that completes each sentence with the most appropriate nuanced expression.","mastery_tag":"vocabulary:c1-nuanced","explanation":"El uso de ''limitado'' en lugar de ''pobre'' puede reflejar una evaluación más cuidadosa de los recursos.","stimulus_en":"The use of ''limited'' instead of ''poor'' can reflect a more ___ assessment of resources.","options":[{"id":"o1","text":"harsh"},{"id":"o2","text":"tactful"},{"id":"o3","text":"blunt"},{"id":"o4","text":"ambiguous"}],"correct_answer":"o2"}')
