INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U8_B2_I5', 'ingles-c1-U8', 10, 'multiple_choice', 'Choose the best option that completes each sentence with the most appropriate nuanced expression.', '{"prompt_es":"Choose the best option that completes each sentence with the most appropriate nuanced expression.","mastery_tag":"vocabulary:c1-nuanced","explanation":"Al proporcionar comentarios, una ''sugerencia'' suele ser más constructiva que una crítica directa.","stimulus_en":"When providing feedback, a ''suggestion'' is often more ___ than a direct critique.","options":[{"id":"o1","text":"harsh"},{"id":"o2","text":"unnecessary"},{"id":"o3","text":"constructive"},{"id":"o4","text":"irrelevant"}],"correct_answer":"o3"}')
