INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U8_B3_I1', 'ingles-c1-U8', 11, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''neither'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''neither'':","mastery_tag":"grammar:c1-ellipsis","explanation":"En esta oración, usamos ''neither'' para sustituir la repetición de ''she didn''t see the problem''.","stimulus_en":"He didn''t see the problem, and she didn''t see it either.\n\nNeither did she see ...","correct_answer":"the problem.","example":"neither"}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U8_B3_I2', 'ingles-c1-U8', 12, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''as well'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''as well'':","mastery_tag":"grammar:c1-ellipsis","explanation":"Aquí, ''as well as'' permite omitir la repetición de ''the project was delayed''.","stimulus_en":"The project was delayed, and the budget was affected too.\n\nThe budget was affected too, ...","correct_answer":"as well as the project.","example":"as well"}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U8_B3_I3', 'ingles-c1-U8', 13, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''also'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''also'':","mastery_tag":"grammar:c1-ellipsis","explanation":"Se utiliza ''also'' para evitar la repetición de ''the presentation'' en la segunda parte de la oración.","stimulus_en":"Tom enjoyed the presentation, and Mary did too.\n\nMary also enjoyed ...","correct_answer":"the presentation.","example":"also"}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U8_B3_I4', 'ingles-c1-U8', 14, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''too'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''too'':","mastery_tag":"grammar:c1-ellipsis","explanation":"El uso de ''too'' aquí nos permite omitir la repetición de ''the report''.","stimulus_en":"I recommend the report, and John recommends it as well.\n\nJohn recommends it, too, ...","correct_answer":"the report.","example":"too"}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U8_B3_I5', 'ingles-c1-U8', 15, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''will'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''will'':","mastery_tag":"grammar:c1-ellipsis","explanation":"Aquí, usamos ''too'' para no repetir ''lead it'' en la segunda parte de la oración.","stimulus_en":"Our team will lead the project, and the marketing team will lead it as well.\n\nThe marketing team will lead the project, ...","correct_answer":"too.","example":"will"}')
