INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U9_B1_I2', 'ingles-c1-U9', 2, 'multiple_choice', 'Which movie do the friends agree is overrated?', '{"prompt_es":"Which movie do the friends agree is overrated?","mastery_tag":"listening:c1-complex-audio","explanation":"They discuss the popularity of ''Avatar'' and mention how they found it lacking.","stimulus_en":"You will hear a conversation between two friends discussing their plans for the weekend. They talk about the weather, their favorite activities, and some recent movies they''ve seen.","options":[{"id":"o1","text":"Inception"},{"id":"o2","text":"Titanic"},{"id":"o3","text":"The Matrix"},{"id":"o4","text":"Avatar"}],"correct_answer":"o4"}')
